import React, { useState } from 'react';
import Header from '../components/Header';
import Background from '../components/Background';
import Footer from '../components/Footer';
import YellowButton from '../components/YellowButton';
import MagicButton from '../components/MagicButton';

const Carte = () => {
  const [filter, setFilter] = useState('all');

  const games = [
    { id: 1, type: 'pc', name: 'Fall Guys', price: 10, imgSrc: 'https://upload.wikimedia.org/wikipedia/en/5/5e/Fall_Guys_cover.jpg' },
    { id: 2, type: 'pc', name: 'Game Name', price: 10, imgSrc: 'game1.png' },
    { id: 3, type: 'pc', name: 'Game Name', price: 10, imgSrc: 'game1.png' },
    { id: 4, type: 'pc', name: 'Game Name', price: 10, imgSrc: 'game1.png' },
    { id: 5, type: 'console', name: 'Game Name', price: 10, imgSrc: 'game1.png' },
    { id: 6, type: 'console', name: 'Game Name', price: 10, imgSrc: 'game1.png' },
    { id: 7, type: 'console', name: 'Game Name', price: 10, imgSrc: 'game1.png' },
    { id: 8, type: 'console', name: 'Game Name', price: 10, imgSrc: 'game1.png' },
  ];

  const filteredGames = filter === 'all' ? games : games.filter(game => game.type === filter);

  return (
    <>
    <Background />
      <Header />
      <section className="homepage">
        <div className="games" id="games">
          <h2>Popular Games</h2>
          <ul>
            <li className={`list ${filter === 'all' ? 'active' : ''}`} onClick={() => setFilter('all')}>All</li>
            <li className={`list ${filter === 'pc' ? 'active' : ''}`} onClick={() => setFilter('pc')}>Pc Games</li>
            <li className={`list ${filter === 'console' ? 'active' : ''}`} onClick={() => setFilter('console')}>Console Games</li>
          </ul>
          <div className="cardBx">
            {filteredGames.map(game => (
              <div className="card" data-item={game.type} key={game.id}>
                <img src={game.imgSrc} alt={game.name} />
                <div className="content">
                  <h4>{game.name}</h4>
                  <div className="info">
                    <p>Pricing<br /><span>${game.price}</span></p>
                    <a href="#"><i className='bx bx-cart-add'></i></a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        </section>
      <Background />
      <Footer />

    </>
  );
};

export default Carte;
